import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import Header from "./Components/Header";
import SearchBar from "./Components/SearchBar";
import ArticleList from "./Components/ArticleList";
import PaginationComponent from "./Components/Pagination";
import { Container } from "react-bootstrap";
import "./App.css";

const App = () => {
  const [articles, setArticles] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchArticles = async (query = "latest", page = 1) => {
    const response = await fetch(
      `http://localhost:5000/api/news?q=${encodeURIComponent(
        query
      )}&page=${page}`
    );
    const data = await response.json();
    setArticles(data.articles);
    setTotalPages(Math.ceil(data.total / 10)); // Assuming 10 articles per page
  };

  useEffect(() => {
    fetchArticles();
  }, []);

  const handleSearch = (query) => {
    fetchArticles(query, 1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    fetchArticles("", page); // Keep search query empty for pagination
  };

  return (
    <div className="app-container">
      <Header />
      <Container>
        <SearchBar onSearch={handleSearch} />
        <ArticleList articles={articles} />
        <PaginationComponent
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      </Container>
    </div>
  );
};

export default App;
